import { createSelector } from '@ngrx/store'
import { Users } from '../models/app.models';
import { allUserss, AppUserProfile } from './app.state';

export const userRootSelector = (state: allUserss) => state.allUsers;
export const allUserData = createSelector(
    userRootSelector,
    (allUsers: Users[]) => {
        return [...allUsers]
    }
)

export const profileRootSelector = (state: AppUserProfile) => state.userProfile
export const userProfile = createSelector(profileRootSelector,
    (userProfile: Users) => {
        return userProfile
    })
