import { Users } from './../models/app.models';
import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { FormBuilder, Validators } from '@angular/forms';
import { Store, select } from '@ngrx/store';
import { uniquedata } from '../state/app.selectors';
import { retrieveUser } from '../state/app.actions';
import { Emitters } from '../emitters/emitters';
import { of } from 'rxjs';

@Component({
  selector: 'app-admin-users',
  templateUrl: './admin-users.component.html',
  styleUrls: ['./admin-users.component.css']
})
export class AdminUsersComponent {


  constructor(private http : HttpClient , private fb : FormBuilder , private store : Store<{ allUsers : Users[] }>) {}

  userdata$ = this.store.pipe(select(uniquedata))
  u : any

  ngOnInit() {
    this.http.get('http://localhost:4000/api/admin/users' , {
      withCredentials : true
    }).subscribe((res) => {
      this.store.dispatch(retrieveUser())
      Emitters.adminAuthEmitter.emit(true)
    })
  }

  searchForm = this.fb.group({
    name : ['']
  })

  newUser = this.fb.group({
    name : ['' , Validators.required],
    email : ['' , [Validators.required , Validators.email]],
    password : ['' , Validators.required]
  })

  adduser() {
    this.u = this.newUser.controls
    if(this.newUser.valid) {
      let newUser = this.newUser.getRawValue()
      this.http.post('http://localhost:4000/api/admin/add-user' , newUser , {
        withCredentials : true
      }).subscribe((res) => {
        console.log('added successfully');
      }, (err) => {
        console.log('something went wrong');
      })
    }
  }

  editUser(id : any) {

  }

  deleteUser(id : any) {
    this.http.post(`http://localhost:4000/api/admin/delete/${ id } ` , {
      withCredentials : true
    }).subscribe((res) => {
      this.store.dispatch(retrieveUser())
      Emitters.adminAuthEmitter.emit(true)
    },(err) => {
      console.log('something went wrong');

    })
  }

  search() {
    let searchVal = this.searchForm.getRawValue()
    this.http.post('http://localhost:4000/api/admin/search' , searchVal , {
      withCredentials : true
    }).subscribe((res : any) => {
      this.userdata$ = of(res)
    },(err) => {
      console.log('something went wrong');
    })
  }

}
<app-admin-nav></app-admin-nav>
<!-- app.component.html -->
<div class="container mt-4">
    <h2 class="mt-5">User Table</h2>
    <div class="form-signin">
        <form [formGroup]="searchForm" (ngSubmit)="search()">
            <input type="text" formControlName="name" placeholder="Search" class="input form-control" />
            <button class="btn btn-primary btn-sm mt-3">Search</button>
        </form>
        <button class="btn btn-primary btn-sm mt-3" data-bs-toggle="modal" data-bs-target="#exampleModall">Add new user</button>
    </div>
    <table class="table table-responsive mt-5">
        <thead>
            <tr>
                <th>Photo</th>
                <th>Name</th>
                <th>Email</th>
                <th>Actions</th>
            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let user of userdata$ | async">
                <td>
                    <img alt="No Photo" [src]="'http://localhost:4000/file/'+ user.image" class="img-thumbnail"
                        width="50" height="50">
                </td>
                <td>{{ user.name }}</td>
                <td>{{ user.email }}</td>
                <td>
                    <button class="btn btn-sm btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal"
                        (click)="editUser(user)">Edit</button>
                    <button class="btn btn-sm btn-danger" (click)="deleteUser(user._id)">Delete</button>
                </td>
            </tr>
        </tbody>
    </table>
</div>
