import { Injectable } from "@angular/core";
import { Actions, createEffect, ofType } from '@ngrx/effects';
import { UserServicesService } from 'src/app/service/user-services.service';
import { LOAD_USERS, loadUsersSuccess } from "./user.action";
import { EMPTY, catchError, exhaustMap, map } from "rxjs";

Injectable()

export class UserEffects {

    constructor(private action$: Actions, private service: UserServicesService) { }


    userlist = createEffect(() =>
        this.action$.pipe(
            ofType(LOAD_USERS),
            exhaustMap((action) => {
                return this.service.getAllUser().pipe(
                    map((data) => {

                        return loadUsersSuccess({ userlist: data })
                    }),
                    catchError((err) => EMPTY)
                )
            })
        )
    )
}
